mapper.update:
UPDATE member_tb
SET password=#{password}, birthday = #{birthday}, email= #{email}, gender= #{gender}
WHERE member_id = #{memberId}

mapper.select:
select member_id, password, birthday, email, gender, image_src
FROM member_tb
WHERE member_id = #{memberId}

