mapper.update:
UPDATE review_tb
SET contents = #{contents} , score=#{score}
WHERE review_id = #{reviewId}

mapper.select:
SELECT review_id, member_id, foodtruck_id, contents, score, writedate
FROM review_tb
WHERE review_id = #{reviewId}

